import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Contact() {

    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [contact, setContact] = useState('');

    const navigate = useNavigate();

    function handleSubmit(event) {
        event.preventDefault();
        axios.post('http://localhost:5000/create', {name, email, contact})
        .then(res => {
            console.log();
        })
    }

  return (
    <div className='d-flex vh-100 bg-secondary justify-content-center ailgn-items-center'>
        <div className='w-50 bg-white rounded p-5'>
            <form onSubmit={handleSubmit}>
                <h2> Add User </h2>
                <br/>

                <div className='mb-5'>
                    <label htmlFor=''> Name: </label>
                    <input type='text' placeholder='Your Name..' className='form-control' onChange={e=> setName(e.target.value)} />
                    <br/>

                    <label htmlFor=''> Email: </label>
                    <input type='email' placeholder='Your Email..' className='form-control' onChange={e=> setEmail(e.target.value)} />
                    <br/>

                    <label htmlFor=''> Contact: </label>
                    <input type='text' placeholder='Contact No..' className='form-control' onChange={e=> setContact(e.target.value)} />
                    <br/>

                    <button className='btn btn-success'> Submit </button>
                </div>
            </form>
        </div>
    </div>
  )
}

export default Contact;
